import React from "react";
import ReactDOM from "react-dom";

// attaching a child to a different parent
const Modal = props => {
  return ReactDOM.createPortal(
    // <div className="ui dimmer modals visible active">
    //   <div className="ui standard modal visible active">
    //     <div className="header">Delete Stream</div>
    //     <div className="content">Are you sure you want to delete this stream?</div>
    //     <div className="actions">
    //       <button className="ui primary button">Delete</button>
    //       <button className="ui button">Cancel</button>
    //     </div>
    //   </div>
    // </div>,
    <div class="ui modal">
  <i class="close icon"></i>
  <div class="header">
    Header
  </div>
  <div class="content">
    <div class="left">
      Some content to the left, usually an image or icon
    </div>
    <div class="right">
      Some content to the right
    </div>
  </div>
  <div class="actions">
    <div class="ui button">
      Cancel
    </div>
    <div class="ui button">
      Okay
    </div>
  </div>
</div>,
    //this is referencing the div under id root (index.html)
    document.getElementById("modal")
  );
}

export default Modal;
